import React, { useState } from 'react'
import styles from '../../styles.module.css'

function Head({
  keys,
  filterable,
  handleQueries,
  sortVirtualData,
  nonFilterCols,
  nonSortCols,
  additionalCols,
  sortable,
  globalSearch,
  generalFilterVirtualData,
  colSpan,
  searchText,
  columns
}) {
  const [sortBy, setSortBy] = useState('')
  const handleSearch = (e) => {
    const newQuery = { key: e.target.name, search: e.target.value }
    handleQueries(newQuery)
  }
  const handleGeneralSearch = (e) => {
    const query = e.target.value
    generalFilterVirtualData(query)
  }
  const handleSort = (key) => {
    // if clicked before chage asc
    const isAsc = sortBy === key ? -1 : 1

    // if clicked bofore we need to change soryBy because we are checking for 3. and more click
    isAsc === -1 ? setSortBy(key + ' clicked') : setSortBy(key)
    sortVirtualData(key, isAsc)
  }

  return (
    <thead>
      {globalSearch && (
        <tr className='rft-gs-tr'>
          <td colSpan={colSpan - 1} />
          <td className={'rft-gs-td ' + styles['rft-gs-td']}>
            <span>{searchText}</span>
            <input
              className={'rft-gs-input ' + styles['rft-gs-input']}
              type='text'
              onChange={handleGeneralSearch}
            />
          </td>
        </tr>
      )}
      <tr>
        {columns
          ? columns.map((col) => (
              <th
                key={col.key}
                onClick={() =>
                  sortable &&
                  !nonSortCols.includes(col.key) &&
                  handleSort(col.key)
                }
              >
                {col.header}
              </th>
            ))
          : keys.map((key) => (
              <th
                key={key}
                onClick={() =>
                  sortable && !nonSortCols.includes(key) && handleSort(key)
                }
              >
                {key.replace('_', ' ')}
              </th>
            ))}
        {!columns &&
          additionalCols.map((additionalCol, index) => (
            <th key={index}>{additionalCol.header.replace('_', ' ')}</th>
          ))}
      </tr>
      {filterable === true && (
        <tr>
          {columns
            ? columns.map((col) =>
                !nonFilterCols.includes(col.key) && col.key ? (
                  <td key={col.key}>
                    <input name={col.key} onChange={handleSearch} type='text' />
                  </td>
                ) : (
                  <td />
                )
              )
            : keys.map((key) =>
                !nonFilterCols.includes(key) ? (
                  <td key={key}>
                    <input name={key} onChange={handleSearch} type='text' />
                  </td>
                ) : (
                  <td />
                )
              )}
          {!columns && additionalCols.map((data, key) => <td key={key} />)}
        </tr>
      )}
    </thead>
  )
}

export default Head
export const getProp = (object, path) => {
  if (path.length === 1) return object[path[0]]
  else if (path.length === 0) {
  } else {
    if (object[path[0]]) return getProp(object[path[0]], path.slice(1))
    else {
      object[path[0]] = {}
      return getProp(object[path[0]], path.slice(1))
    }
  }
}
import React from 'react'
import { getProp } from '../util/functions'
function Body({ data, pageSize, page, keys, additionalCols, columns }) {
  return (
    <tbody>
      {data
        .slice(pageSize * (page - 1), pageSize * page)
        .map((singleData, index) => {
          return columns ? (
            <tr key={index}>
              {columns.map((col) => (
                <td key={col.key}>
                  {col.td
                    ? col.td(singleData)
                    : getProp(singleData, col.key.split('.')).toString()}
                </td>
              ))}
            </tr>
          ) : (
            <tr key={index}>
              {keys.map((key) => (
                <td key={key}>{singleData[key].toString()}</td>
              ))}
              {additionalCols.map((col, index) => (
                <td key={index}>{col.td(singleData)}</td>
              ))}
            </tr>
          )
        })}
    </tbody>
  )
}

export default Body
import React from 'react'
import styles from '../../styles.module.css'

function Pagination({
  handlePageChange,
  page,
  maxPageSize,
  dataCount,
  pageSizeOptions,
  pageSize,
  handlePageSizeChange,
  filteredDataCount,
  previousText,
  nextText,
  rowsText,
  pageText,
  ofText,
  totalDataText,
  filteredDataText,
  colSpan
}) {
  return (
    <tfoot>
      <tr>
        <td colSpan={colSpan}>
          <div className={'rft-pagination ' + styles['rft-pagination']}>
            <button
              className={
                'rft-pagination-button ' + styles['rft-pagination-button']
              }
              onClick={() => handlePageChange(page - 1)}
            >
              {previousText}
            </button>
            <span
              className={'rft-filtered-text ' + styles['rft-filtered-text']}
            >
              {filteredDataText} {filteredDataCount}
            </span>

            <span
              className={'rft-totaldata-text ' + styles['rft-totaldata-text']}
            >
              {totalDataText} {dataCount}
            </span>

            <div>
              <label className={'rft-page-text ' + styles['rft-page-text']}>
                {pageText}
              </label>
              <input
                type='number'
                value={page}
                onChange={(e) => handlePageChange(Number(e.target.value))}
                max={maxPageSize}
                min={1}
              />
              <label className={'rft-page-text ' + styles['rft-page-text']}>
                {ofText} {maxPageSize}
              </label>
            </div>

            <div>
              <select
                onChange={(e) => handlePageSizeChange(Number(e.target.value))}
                value={pageSize}
              >
                {pageSizeOptions.map((pso) => (
                  <option key={pso} value={pso}>
                    {rowsText} {pso}
                  </option>
                ))}
              </select>
            </div>
            <button
              className={
                'rft-pagination-button ' + styles['rft-pagination-button']
              }
              onClick={() => handlePageChange(page + 1)}
            >
              {nextText}
            </button>
          </div>
        </td>
      </tr>
    </tfoot>
  )
}

export default Pagination
