import React /*,{Component}*/ from 'react'
//import {BootstrapTable, TableHeaderColumn} from 'react-bootstrap-table'
//import '../node_modules/react-bootstrap-table/css/react-bootstrap-table.css'
//import './Table.css'
import ReactFlexyTable from "react-flexy-table"
import "react-flexy-table/dist/index.css"
import axios from 'axios'

class ForecastTable extends React.Component {



    constructor(props) {
        super(props)

        this.hardCodeResult = [true, false, false, false, true]
        this.county = 'none';
        this.data = [
          { Day: 'n/a', FirePrediction: 'n/a'},
        ]

        this.handleCountyEnter = this.handleCountyEnter.bind(this)
        this.handleCountyBoxChange = this.handleCountyBoxChange.bind(this)
    }

    handleCountyBoxChange(e){
        this.county = e.target.value
    }

    handleCountyEnter(e) {
        console.log(this.county)
        axios.get(`localhost:10000/api/forestFireForecast/${this.county}`)
          .then(res => {
              console.log(res);
              console.log(res.data);
              var result = res.data
          }).catch(e => {
            console.log('error')
            console.log(e)
            var result = this.hardCodeResult
          })

        //let result = this.hardCodeResult; // would have been the result of axios.get
        let today = new Date()
        this.data = []
        for (let i = 0; i < result.length; i++)
          this.data.push({Day: (today.getMonth()+1)+"/"+(today.getDate()+(i)) , FirePrediction: result[i]} )


        this.setState({}) // force re-render
      }

    render() {
      return (
        <div className="ForecastTable">
           <h1>Forest Fire Predicting App</h1>
           <br></br>
           <br></br>
           <br></br>
           What is your county?
          <input
            type="text"
            onChange={this.handleCountyBoxChange}
          />
          <input 
            type="button"
            value="Submit"
            onClick={this.handleCountyEnter}
          /> 
        <div>
           <br></br>
           <br></br>
           <br></br>
          Forecast Table for {this.county}
          <ReactFlexyTable data={this.data}/>
        </div>
        </div>
      )
    }
}

export default ForecastTable;
