import React /*,{Component}*/ from 'react'
import {BootstrapTable, TableHeaderColumn} from 'react-bootstrap-table'
import '../node_modules/react-bootstrap-table/css/react-bootstrap-table.css'
import './Table.css'

class ForecastTable extends React.Component {

    constructor(props) {
        super(props)
        this.county = 'none';
        this.handleCountyEnter = this.handleCountyEnter.bind(this)
        this.handleCountyBoxChange = this.handleCountyBoxChange.bind(this)
    }

    handleCountyBoxChange(e){
        this.county = e.target.value
    }

    handleCountyEnter(e) {
        console.log(this.county)
        // send submission request
    }

    render() {
      return (
        <div className="ForecastTable">
           <br></br>
           <br></br>
           <br></br>
           What is your county?
          <input
            type="text"
            onChange={this.handleCountyBoxChange}
          />
          <input 
            type="button"
            value="Submit"
            onClick={this.handleCountyEnter}
          /> 
            <h2>(insert forecast table here)</h2>
        <div>
          <BootstrapTable data={this.props.data}>
            <TableHeaderColumn isKey dataField='id'>
              ID
            </TableHeaderColumn>
            <TableHeaderColumn dataField='Day'>
              Day
            </TableHeaderColumn>
            <TableHeaderColumn dataField='FirePrediction'>
              Fire Prediction
            </TableHeaderColumn>
          </BootstrapTable>
        </div>
        </div>
      )
    }
}

export default ForecastTable;
